---
import "./index.css";
import type { HTMLAttributes } from "astro/types";
import type { Dataset } from "../types";
import Picture from "../Picture/index.astro";
import mapDataset from "../utilities/mapDataset";
import type { Props as PictureProps } from "../Picture/index.astro";

export type Props = HTMLAttributes<"a"> & {
  dataset?: Dataset;
  href?: string;
  image?: PictureProps;
  isInvertible?: boolean;
  title: string;
};

const {
  dataset,
  href,
  image = {} as PictureProps,
  isInvertible,
  title,
} = Astro.props;
const { alt, filename, src, width, ...imageAttrs } = image;

const data = mapDataset(dataset);
const description = await Astro.slots.render("default");
---

<section class={`card${isInvertible ? " allow-invert" : ""}`} {...data}>
  {
    href ? (
      <a class:list={["sb-group"]} href={href}>
        <header>
          <h3>{title}</h3>
        </header>
        {image.filename ? (
          <Picture
            alt={alt}
            filename={filename}
            src={src}
            width={width}
            {...imageAttrs}
          />
        ) : null}
      </a>
    ) : (
      <div class:list={["sb-group"]}>
        <header>
          <h3>{title}</h3>
        </header>
        {image.filename ? (
          <Picture
            alt={alt}
            filename={filename}
            src={src}
            width={width}
            {...imageAttrs}
          />
        ) : null}
      </div>
    )
  }
  <div class="card-description">
    <Fragment set:html={description} />
  </div>
</section>
