---
import type { Field as Props } from "../../../types";
import TextualInput from "../elements/TextualInput/index.astro";
import Label from "../elements/Label/index.astro";
import generateShortId from "../../utilities/generateShortId";
import mapDataset from "../../utilities/mapDataset";

const {
	cssClasses = [],
	dataset,
	elementAttributes = {},
	form,
	help,
	helpAttributes = {},
	id = generateShortId(),
	label,
	labelAttributes = {},
	name,
	required = false,
	...attrs
} = Astro.props;

const css = ["sb-field", ...cssClasses].join(" ");
const data = mapDataset(dataset);
const helpId = `${id}_help`;
const fieldHelp = help || Astro.slots.render("help");
const labelId = `${id}_label`;
const fieldLabel = label || Astro.slots.render("default");
---

<div {...attrs} class={css} {...data}>
	<Label
		{...labelAttributes}
		help={help}
		helpAttributes={helpAttributes}
		helpId={helpId}
		for={id}
		form={form}
		id={labelId}
		required={required}
	>
		<Fragment set:html={fieldLabel} />
		<Fragment set:html={fieldHelp} slot="help" />
	</Label>
	<TextualInput
		{...elementAttributes}
		form={form}
		id={id}
		name={name}
		required={required}
		type="email"
	/>
</div>
