 .sb-group {

commits.content:
body {
	display: grid;
	grid-template-columns: 1fr 1fr;
	padding: 3rem;
}

form {
	align-items: flex-start;
	display: flex;
	flex-direction: column;
	gap: 1rem;
	justify-content: flex-start;
}

.field {
	margin: 0.5rem 0;
}

fieldset.field {
	border: 1px dotted gray;
	border-radius: 4px;
	padding: 1rem;
	margin-top: 1.5rem;
	position: relative;
}

fieldset.field > legend {
	align-items: flex-start;
	display: flex;
	flex-direction: column;
	gap: 0.5rem;
	justify-content: space-between;
	font-family: sans-serif;
	font-size: 1.2rem;
	font-weight: bold;
	left: -5px;
	position: absolute;
	top: -1.7rem;
}

fieldset.field > legend {
	margin-top: 0;
}

.group {
	align-items: center;
	display: flex;
	flex-wrap: wrap;
	gap: 0.7rem;
	justify-content: flex-start;
	margin-top: 0.5rem;
}

[data-sb-instant] .group {
	align-items: flex-start;
	flex-direction: column;
}

.group > label {
	align-items: center;
	display: flex;
	font-family: sans-serif;
	gap: 0.3rem;
	justify-content: flex-start;
}

:not(.group) > label {
	display: block;
	font-family: sans-serif;
	font-size: 1.2rem;
	font-weight: bold;
	margin-bottom: 0.3rem;
}

code {
	display: block;
	margin: 2rem 0 2rem 3rem;
}

pre {
	font-family: sans-serif;
	font-size: 1.2rem;
}

input,
select,
textarea {
	border: 2px solid forestgreen;
	border-radius: 4px;
	padding: 0.3rem 0.6rem;
}

input[type=number] {
	text-align: right;
}

input[data-sb-duration] {
	width: 5ch;
}

blockquote {
	margin: 0 0 0 2rem;
}

.help {
	color: slateblue;
	font-weight: 500;
	margin: 0.5rem 0;
}
