commits.content:
---
import type { MemberField as Props, RadioGroupOption } from "../../../types";
import Label from "../elements/Label/index.astro";
import RadioGroup from "../elements/RadioGroup/index.astro";
import Select from "../elements/Select/index.astro";
import generateShortId from "../../utilities/generateShortId";
import mapDataset from "../../utilities/mapDataset";
import { MEMBER_FIELD_PIVOT } from "../../../constants";

const {
	cssClasses = [],
	dataset,
	elementAttributes = {},
	form,
	help,
	helpAttributes = {},
	id = generateShortId(),
	includeNull,
	label,
	labelAttributes = {},
	name,
	options = [] as Array<RadioGroupOption>,
	required = false,
	selected,
	...attrs
} = Astro.props;

function getOptionsCount(opts: Array<RadioGroupOption>): number {
	return opts.reduce(
		(acc, opt) => acc + (opt.options?.length ? opt.options.length + 1 : 1),
		0
	);
}

const css = ["sb-field", ...cssClasses].join(" ");
const data = mapDataset(dataset);
const helpId = `${id}_help`;
const fieldHelp = help || Astro.slots.render("help");
const labelId = `${id}_label`;
const fieldLabel = label || Astro.slots.render("label");
const count = getOptionsCount(options);
const LIMIT = import.meta.env.MEMBER_FIELD_PIVOT || MEMBER_FIELD_PIVOT;
---

{
	count > LIMIT ? (
		<div {...attrs} class={css} {...data}>
			<Label
				{...labelAttributes}
				help={help}
				helpAttributes={helpAttributes}
				helpId={helpId}
				for={id}
				form={form}
				id={labelId}
				required={required}
			>
				<Fragment set:html={fieldLabel} />
				<Fragment set:html={fieldHelp} slot="help" />
			</Label>
			<Select
				{...elementAttributes}
				form={form}
				id={id}
				includeNull={includeNull}
				name={name}
				options={options}
				selected={selected}
			/>
		</div>
	) : (
		<RadioGroup
			{...elementAttributes}
			form={form}
			help={help}
			helpAttributes={helpAttributes}
			helpId={helpId}
			id={id}
			includeNull={includeNull}
			labelAttributes={labelAttributes}
			name={name}
			options={options}
			selected={selected}
		>
			<Fragment set:html={fieldLabel} />
			<Fragment set:html={fieldHelp} slot="help" />
		</RadioGroup>
	)
}
