---
import type { RadioGroup as Props } from "../../../../types";
import generateShortId from "../../../utilities/generateShortId";
import mapDataset from "../../../utilities/mapDataset";
import Radio from "../Radio/index.astro";

const {
	["aria-live"]: live = "assertive",
	cssClasses = [],
	dataset,
	form,
	help,
	helpAttributes = {},
	id = generateShortId(),
	includeNull,
	label,
	labelAttributes,
	name,
	options,
	required,
	selected,
	...attrs
} = Astro.props;

const css = ["sb-radio-group", ...cssClasses].join(" ");
const data = mapDataset(dataset);
const hlp = help || (await Astro.slots.render("help"));
const helpId = `${id}_help`;
const legend = label || (await Astro.slots.render("default"));
const legendId = `${id}_legend`;
const req = required ? { dataset: { required } } : {};
const opt = required ? {} : { "data-sb-optional": "" };
---

<fieldset
	{...attrs}
	class={css}
	{...data}
	form={form}
	id={id}
	name={name}
	{...req}
	role="radiogroup"
>
	<legend
		{...labelAttributes}
		aria-live={live}
		class={css}
		{...data}
		id={legendId}
		{...opt}
	>
		<span class="sb-label-content" set:html={legend} />
		<span
			{...helpAttributes}
			aria-live={live}
			class="sb-help"
			id={helpId}
			role="status"
			set:html={hlp}
		/>
	</legend>
	<div class="sb-group">
		{
			options.map(({ label, value, ...attrs }) => (
				<Radio
					{...attrs}
					checked={Boolean(selected) && selected === value}
					form={form}
					legendId={legendId}
					name={name}
					required={required}
					value={value}
				>
					{label}
				</Radio>
			))
		}
		{
			includeNull ? (
				<Radio
					{...attrs}
					checked={Boolean(selected) && selected === ""}
					form={form}
					legendId={legendId}
					name={name}
					value=""
				>
					N/A
				</Radio>
			) : null
		}
	</div>
</fieldset>
