commits.content:
fieldset {
  align-items: flex-start;
  background-color: var(--sb-fieldset-background-color, hsl(212, 25%, 93%));
  border-color: var(--sb-fieldset-border-color, hsl(212, 25%, 71%));
  border-radius: 5px;
  border-style: dotted;
  border-width: 1px;
  color: var(--sb-fieldset-color, hsl(212, 25%, 15%));
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-bottom: var(--space-s);
  padding: 1rem;
}

button:focus,
input:focus,
select:focus,
textarea:focus {
  outline: 2px var(--sb-form-outline-color, hsl(200, 82%, 40%));
}

input:focus {
  box-shadow: 1px 1px 8px 1px var(--sb-form-outline-color, hsl(200, 82%, 40%));
}

.sb-form-field {
  margin: var(--sb-form-field-margin, 0);
  max-width: calc(100vw - 2rem);
}

input:not([type="checkbox"], [type="radio"]),
label,
output,
select,
textarea {
  max-width: 80vw;
}

/* button,
input:not([type="checkbox"], [type="radio"]),
select {
  min-height: 3.125rem;
} */

.sb-boolean-wrapper label {
  display: grid;
  gap: 1em;
  grid-template-columns: 1em auto;
  margin-top: 1rem;
  width: min(86vw, 100%);
}

.sb-boolean-wrapper label > * {
  margin: 0;
}

.sb-boolean-wrapper input[type="checkbox"] {
  appearance: none;
  border-radius: 0.15em;
  border: var(--sb-boolean-field-border, 0.15em solid hsl(212, 25%, 23%));
  color: var(--sb-boolean-field-color, hsl(212, 25%, 23%));
  display: grid;
  font: inherit;
  height: 1.15em;
  margin: 0.15em 0 0;
  place-content: center;
  transform: translateY(-0.075em);
  vertical-align: middle;
  width: 1.15em;
  width: auto;
}

.sb-boolean-wrapper input[type="checkbox"]:checked {
  background-color: var(
    --sb-boolean-field-background-color-checked,
    var(--color-gray-7)
  );
}

.sb-boolean-wrapper input[type="checkbox"]::before {
  background-color: var(
    --sb-boolean-field-background-color-before,
    var(--color-gray-7)
  );
  box-shadow: var(
    --sb-boolean-field-box-shadow-before,
    inset 1rem 1rem var(--color-hunter-green)
  );
  clip-path: polygon(14% 44%, 0 65%, 50% 100%, 100% 16%, 80% 0%, 43% 62%);
  content: "";
  height: 0.65em;
  transform-origin: bottom left;
  transform: scale(0);
  transition: 120ms transform ease-in-out;
  width: 0.65em;
}

.sb-boolean-wrapper input[type="checkbox"]:checked::before {
  background-color: var(
    --sb-boolean-field-background-color-checked-before,
    var(--color-gray-7)
  );
  transform: scale(1);
}

.sb-boolean-wrapper input[type="checkbox"]:focus {
  outline-offset: max(2px, 0.15em);
  outline: var(
    --sb-boolean-field-outline,
    max(2px, 0.15em) solid var(--color-hunter-green)
  );
}

.sb-boolean-wrapper input:disabled {
  color: var(--sb-contact-form-input-color-disabled, hsl(212, 25%, 46%));
  cursor: not-allowed;
}

fieldset legend {
  background-color: var(
    --sb-fieldset-legend-background-color,
    hsl(212, 25%, 93%)
  );
  border-radius: 5px;
  border: 2px solid var(--sb-fieldset-legend-border-color, hsl(212, 25%, 71%));
  color: var(--sb-fieldset-legend-color, hsl(212, 25%, 15%));
  font-family: var(--sb-fieldset-legend-font-family, inherit);
  font-size: var(--sb-fieldset-legend-font-size, 1.25rem);
  font-weight: 400;
  padding: 0.25rem 0.5rem;
}

fieldset.grouped legend,
fieldset fieldset legend {
  font-family: var(--sb-fieldset-legend-font-family, inherit);
  font-size: var(--sb-fieldset-legend-font-size-nested, 1rem);
}

.sb-form-field {
  position: relative;
}

.sb-fieldset-wrapper.required legend,
.sb-form-field.required label {
  color: var(--sb-form-label-color-required, var(--color-hunter-green));
  font-weight: var(--sb-form-label-font-weight, 600);
  padding-bottom: 0.25rem;
}

.sb-fieldset-wrapper.required.readonly legend,
.sb-form-field.required.readonly label {
  color: var(--sb-form-label-color-optional, var(--color-y-ln-mn));
  padding-bottom: 0.25rem;
}

.sb-fieldset-wrapper label,
.sb-form-field label {
  color: var(--sb-form-label-color-optional, var(--color-y-ln-mn));
}

.sb-form-field label {
  display: block;
  font-family: var(
    --sb-form-label-font-family,
    "Iowan Old Style",
    "Palatino Linotype",
    "URW Palladio L",
    P052,
    serif
  );
  padding-left: 0.2rem;
}

.sb-fieldset-wrapper label {
  align-items: center;
  display: flex;
  font-family: var(
    --sb-form-fieldset-wrapper-label-font-family,
    Inter,
    Roboto,
    "Helvetica Neue",
    "Arial Nova",
    "Nimbus Sans",
    Arial,
    sans-serif
  );
  gap: 0.4rem;
}

.sb-fieldset-wrapper input:not([type="checkbox"], [type="radio"]),
.sb-fieldset-wrapper select,
.sb-fieldset-wrapper textarea,
.sb-form-field input:not([type="checkbox"], [type="radio"]),
.sb-form-field select,
.sb-form-field textarea {
  font-family: var(
    --sb-form-field-font-family,
    Inter,
    Roboto,
    "Helvetica Neue",
    "Arial Nova",
    "Nimbus Sans",
    Arial,
    sans-serif
  );
  font-size: var(--sb-form-field-font-size, clamp(1.00rem, calc(0.91rem + 0.47vw), 1.38rem));
  font-variant: proportional-nums oldstyle-nums;
  font-weight: 500;
  padding: 0.5rem 0.25rem;
}

.sb-fieldset-wrapper input,
.sb-fieldset-wrapper select,
.sb-fieldset-wrapper textarea,
.sb-form-field input,
.sb-form-field select,
.sb-form-field textarea {
  background-color: var(--sb-form-field-background-color, hsl(0, 100%, 100%));
  border-radius: 5px;
  border: 2px dotted var(--sb-form-field-border-color, hsl(212, 25%, 46%));
  color: var(--sb-form-field-color, hsl(212, 25%, 15%));
  margin: 0;
  position: relative;
}

.sb-fieldset-wrapper input[type="checkbox"],
.sb-fieldset-wrapper input[type="radio"],
.sb-form-field input[type="checkbox"],
.sb-form-field input[type="radio"] {
  height: 1.2rem;
  width: 1.2rem;
  margin-right: 0.25rem;
}

.sb-fieldset-wrapper label:has(input[type="checkbox"], input[type="radio"]),
.sb-form-field label:has(input[type="checkbox"], input[type="radio"]) {
  align-items: center;
  display: flex;
  gap: 0.2rem;
  justify-content: flex-start;
}

.sb-fieldset-wrapper input:required,
.sb-fieldset-wrapper select:required,
.sb-fieldset-wrapper textarea:required,
.sb-form-field input:required,
.sb-form-field select:required,
.sb-form-field textarea:required {
  border: 4px double
    var(--sb-form-field-border-color-required, hsl(111, 46%, 60%));
}

.sb-fieldset-wrapper input:focus,
.sb-fieldset-wrapper select:focus,
.sb-fieldset-wrapper textarea:focus,
.sb-form-field input:focus,
.sb-form-field select:focus,
.sb-form-field textarea:focus {
  background-color: var(
    --sb-form-field-background-color-focus,
    hsl(0, 100%, 100%)
  );
  border-color: var(--sb-form-field-border-color-focus, hsl(36, 97%, 66%));
  box-shadow: none;
  color: var(--sb-form-field-color-focus, hsl(212, 25%, 31%));
}

.show .sb-fieldset-wrapper input:invalid,
.show .sb-fieldset-wrapper select:invalid,
.show .sb-fieldset-wrapper textarea:invalid,
.show .sb-fieldset-wrapper input:required:invalid,
.show .sb-fieldset-wrapper select:required:invalid,
.show .sb-fieldset-wrapper textarea:required:invalid,
.show .sb-form-field input:invalid,
.show .sb-form-field select:invalid,
.show .sb-form-field textarea:invalid,
.show .sb-form-field input:required:invalid,
.show .sb-form-field select:required:invalid,
.show .sb-form-field textarea:required:invalid {
  background: repeating-linear-gradient(
    45deg,
    var(--sb-background-stripe-invalid-light),
    var(--sb-background-stripe-invalid-light) 3px,
    var(--sb-background-stripe-invalid-dark) 3px,
    var(--sb-background-stripe-invalid-dark) 6px
  );
  border: var(--sb-form-field-border-invalid, 4px dashed hsl(36, 97%, 66%));
}

.sb-fieldset-wrapper input:valid,
.sb-fieldset-wrapper select:valid,
.sb-fieldset-wrapper textarea:valid,
.sb-fieldset-wrapper input:required:valid,
.sb-fieldset-wrapper select:required:valid,
.sb-fieldset-wrapper textarea:required:valid,
.sb-form-field input:valid,
.sb-form-field select:valid,
.sb-form-field textarea:valid,
.sb-form-field input:required:valid,
.sb-form-field select:required:valid,
.sb-form-field textarea:required:valid {
  border-color: var(--sb-form-field-border-color-valid, hsl(111, 46%, 60%));
}

.show .sb-fieldset-wrapper input:valid + span::after,
.show .sb-fieldset-wrapper select:valid + span::after,
.show .sb-fieldset-wrapper textarea:valid + span::after,
.show .sb-form-field input:valid + span::after,
.show .sb-form-field select:valid + span::after,
.show .sb-form-field textarea:valid + span::after {
  color: var(--sb-form-field-valid-symbol-color, hsl(111, 46%, 24%));
  content: var(--sb-form-field-valid-symbol, "\2002\2714");
  display: inline-block;
  font-weight: 700;
}

.show .sb-fieldset-wrapper input:invalid + span::after,
.show .sb-fieldset-wrapper select:invalid + span::after,
.show .sb-fieldset-wrapper textarea:invalid + span::after,
.show .sb-fieldset-wrapper input:required:invalid + span::after,
.show .sb-fieldset-wrapper select:required:invalid + span::after,
.show .sb-fieldset-wrapper textarea:required:invalid + span::after,
.show .sb-form-field input:invalid + span::after,
.show .sb-form-field select:invalid + span::after,
.show .sb-form-field textarea:invalid + span::after,
.show .sb-form-field input:required:invalid + span::after,
.show .sb-form-field select:required:invalid + span::after,
.show .sb-form-field textarea:required:invalid + span::after {
  color: var(--sb-form-field-invalid-symbol-color, hsl(4, 57%, 52%));
  content: var(--sb-form-field-invalid-symbol, "\2002\2717");
  display: inline-block;
  font-weight: 700;
}

.sb-fieldset-wrapper input:required:focus,
.sb-fieldset-wrapper select:required:focus,
.sb-fieldset-wrapper textarea:required:focus,
.sb-form-field input:required:focus,
.sb-form-field select:required:focus,
.sb-form-field textarea:required:focus {
  background: var(--sb-form-field-background-color, hsl(0, 100%, 100%));
  border-color: var(--sb-form-field-border-color-required, hsl(111, 46%, 60%));
  color: var(--sb-form-field-color-required, hsl(111, 46%, 24%));
}

.sb-form-field textarea {
  margin-bottom: -0.5rem;
}

.sb-fieldset-wrapper *[readonly],
.sb-form-field *[readonly] {
  background-color: transparent;
  border-color: transparent;
  color: var(--sb-form-field-color-readonly, hsl(212, 57%, 32%));
  padding-left: 0;
}

.sb-fieldset-wrapper
  input[type="number"]
  form
  .sb-form-field
  input[type="number"] {
  text-align: right;
}

.sb-fieldset-wrapper output,
.sb-form-field output {
  display: block;
  font-family: var(
    --sb-form-help-font-family,
    "Iowan Old Style",
    "Palatino Linotype",
    "URW Palladio L",
    P052,
    serif
  );
  font-size: 1rem;
  margin: 0.2rem 0 0.5rem;
  min-height: var(--sb-form-help-min-height, 1.5rem);
  padding-left: 0.2rem;
}

.sb-fieldset-wrapper output.invalid,
.sb-form-field output.invalid {
  color: hsl(321, 60%, 32%);
}

.sb-fieldset-wrapper .readonly,
.sb-form-field .readonly {
  margin-top: 0.3rem;
  padding-left: 0.2rem;
}

.multiple-input {
  align-items: stretch;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  justify-content: flex-start;
  margin-top: 0;
}

.sb-field-edit-toggle {
  position: absolute;
  right: -4.5rem;
  top: 1.25rem;
}

.sb-button-bar button {
  align-items: center;
  background-color: var(
    --sb-form-button-background-color-submit,
    hsl(111, 46%, 24%)
  );
  border-radius: 5px;
  border: 2px solid
    var(--sb-form-button-border-color-submit, hsl(111, 46%, 24%));
  color: var(--sb-form-button-color-submit, hsl(212, 25%, 98%));
  display: flex;
  font-family: var(
    --sb-form-button-font-family,
    "Iowan Old Style",
    "Palatino Linotype",
    "URW Palladio L",
    P052,
    serif
  );
  font-kerning: normal;
  font-size: var(--sb-form-button-font-size, 1rem);
  font-variant-caps: titling-caps;
  font-weight: 700;
  justify-content: center;
  letter-spacing: 1px;
  padding: 0 0.75rem;
  text-transform: uppercase;
}

.sb-button-bar button:disabled,
.sb-button-bar button:disabled:hover {
  background-color: var(
    --sb-form-button-background-color-disabled,
    hsl(212, 25%, 83%)
  );
  border: 2px solid
    var(--sb-form-button-border-color-disabled, hsl(212, 25%, 71%));
  color: var(--sb-form-button-color-disabled, hsl(212, 25%, 46%));
  cursor: not-allowed;
}

.sb-button-bar button:focus,
.sb-button-bar button:hover {
  background-color: var(
    --sb-form-button-background-color-submit-hover,
    hsl(212, 25%, 98%)
  );
  border: 2px solid
    var(--sb-form-button-border-color-submit-hover, hsl(111, 46%, 24%));
  color: var(--sb-form-button-color-submit-hover, hsl(111, 46%, 24%));
}

form fieldset.sb-fieldset-wrapper {
  border: none;
  max-width: 80vw;
  min-width: 99.5%;
  padding: 0;
}

form fieldset.sb-fieldset-wrapper legend {
  background-color: transparent;
  border: none;
  color: var(--sb-duration-field-legend-color, hsl(212, 25%, 15%));
  display: block;
  font-family: var(
    --sb-duration-field-font-family,
    Inter,
    Roboto,
    "Helvetica Neue",
    "Arial Nova",
    "Nimbus Sans",
    Arial,
    sans-serif
  );
  padding: 0;
}

form fieldset.sb-fieldset-wrapper input {
  margin-right: 0.3rem;
  max-width: 6ch;
  text-align: right;
}
